import { Component } from 'react';
import { Col, Image, Row, Typography, Tag, Rate, Progress, Flex, Spin } from 'antd';
import { format, parseISO } from 'date-fns';

const { Title, Text, Paragraph } = Typography;

import './Movie.css';

export default class Movie extends Component {
  getGenresItems = (genres) => {
    return genres.map((genre) => {
      return <Tag key={genre.id}>{genre.name}</Tag>;
    });
  };

  getShortedOverview = (text, maxLength) => {
    if (Text.length <= maxLength) return text;

    const lastSpaceIndex = text.lastIndexOf(' ', maxLength);
    if (lastSpaceIndex !== -1) {
      return text.slice(0, lastSpaceIndex) + '...';
    }
    return text.slice(0, maxLength) + '...';
  };

  render() {
    const { movie, genres, loading } = this.props;
    const rows = movie.title.length < 23 ? { rows: 4 } : { rows: 3 };
    const rateAverage = (movie.vote_average * 10).toFixed(0);
    const genresTags = this.getGenresItems(genres);
    const dateFromServer = movie.release_date;
    const parsedDate = parseISO(dateFromServer);
    const formattedDate = format(parsedDate, 'MMMM d, yyyy');

    if (loading) {
      return (
        <Row className="movieCard" wrap={false}>
          <Spin className="spin" />
        </Row>
      );
    }

    return (
      <Row className="movieCard" wrap={false}>
        <Col>
          <Image
            width={180}
            height={280}
            src={`https://image.tmdb.org/t/p/original${movie.poster_path}`}
            className="item-image"
            fallback="data:image/png;base64,"
          />
        </Col>
        <Col className="movieText">
          <Flex>
            <Title className="title" level={4}>
              {movie.title}
            </Title>
            <Progress
              className="progressBar"
              type="circle"
              percent={rateAverage}
              size={40}
              format={(percent) => percent / 10}
            />
          </Flex>
          <Text className="releaseDate" type="secondary">
            {formattedDate}
          </Text>
          <div className="tags">{genresTags}</div>
          <Paragraph ellipsis={rows}>{this.getShortedOverview(movie.overview, 140)}</Paragraph>

          <Rate className="rate" allowHalf defaultValue={movie.vote_average} count={10} style={{ fontSize: '15px' }} />
        </Col>
      </Row>
    );
  }
}
